if [[ ! -f ./twistcli-darwin || $(./twistcli-darwin --version) != *"20.04.177"* ]]; then curl --progress-bar -L -k --header 'authorization: Bearer '"$TF_VAR_pcc_token"'' $TF_VAR_pcc_url/api/v1/util/osx/twistcli > twistcli-darwin; chmod +x twistcli-darwin; fi; ./twistcli-darwin defender install kubernetes --namespace twistlock --monitor-service-accounts --collect-pod-labels --token $TF_VAR_pcc_token --address $TF_VAR_pcc_url --cluster-address $TF_VAR_pcc_cluster_address